import React, { Component } from 'react';
import './App.css';

class Button extends Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};
    }

    render() {
        return(
            <form >
                <label>
                Name:
                <input type="text" value={this.state.value} />
                </label>
                <input type="submit" value="Submit" />
            </form>
        );
    }
}

export default Button;
