import styled from 'styled-components'
import { NavLink as Link } from 'react-router-dom'
import {FaBars} from 'react-icons/fa'

export const Nav = styled.nav`
  background-color: #000;
  height: 80px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  
  z-index: 10;
`

export const NavLink = styled(Link)`
  color: #fff;
  display: block;
  align-items: center;
  text-decoration: none;
  margin: 0px 40px 0px 0px;
  cursor: pointer;
  height:80px;
  &.active {
    color: #15cdfc;
    background:#555;
  }
`

export const NavMenu = styled.div`
  display: flex;
  width:100%;
  height:80px;
  align-items: center;
`


export const Bars = styled(FaBars)`
  display: none;
  color: #fff;

  @media screen and (max-width : 768px) {
    display: block;
    position: absolute;
    top: 0;
    right: 0;
    transform: translaste(-100%, 75%);
    font-size: 1.8rem;
    cursor: pointer;
  }
`


export const NavBtn = styled.nav`
  display: flex;
  align-items: center;
  margin-right: 24px;

  @media screen and (max-width: 768px) {
    display: none;
  }
`

export const NavBtnLink = styled(Link)`
  border-radius: 4px;
  background: #256ce1;
  padding: 10px 22px;
  color: #fff;
  border: none;
  outline:none;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  text-decoration: none;

  &:hover {
    transition: all 0.2s ease-in-out;
    background: #fff;
    color: #010606;
  }
`;
