import React, { Component } from 'react';
import './Form.css';

class Form extends Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};
    }

    render() {
        return(
            <div className ="Form">
                <header>
                    <form >
                        <label>
                            Name:
                            <input 
                                type="text" 
                                value={this.state.value} 
                            />
                        </label>
                        <input 
                            type="submit" 
                            value="Submit" 
                        />
                    </form>
                </header>
            </div>
        );
    }
}

export default Form;
