import React, { Component } from 'react';
import './Button.css';

class Button extends Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};
    }

    render() {
        return(
            <div>
                <form >
                    <label>
                        Name:
                        <input 
                            type="text" 
                            value={this.state.value} 
                        />
                    </label>
                    <input 
                        type="submit" 
                        value="Submit" 
                    />
                </form>
            </div>
        );
    }
}

export default Button;
