import React, { Component } from 'react';
import './Menu.css';
class Menu extends Component {

  constructor(props) {
    super(props);
    this.state = {option : ''};
    this.optionOne = this.optionOne.bind(this);
  }

  optionOne(event) {
    this.setState({email: event.target.value});
  }

  render() {
    return(
      <div>
        <ul>
          <li>
            O1
            <Button onCLick={optionOne}>
              Option 1
            </Button>
            </li>
          <li>Option 2</li>
        </ul>
      </div>
    );
  }

}

export default Menu;
